mapper.select:
select cda.id as cda_id, cda.semester as cda_semester, cda.coursedesign as cda_coursedesign, cda.coursedesign_name as cda_coursedesign_name, cda.major as cda_major, cda.grade as cda_grade, cda.class_num as cda_class_num, cda.week_num as cda_week_num, cda.preday as cda_preday, cda.workload as cda_workload, cda.period as cda_period, cda.campus as cda_campus, cda.creator as cda_creator, cda.create_date as cda_create_date, cda.remark as cda_remark, cda.status as cda_status, cd.id as cd_id, cd.name as cd_name, m.id as m_id, m.name as m_name, u.id as u_id, u.name as u_name from t_cdesign_account cda left join t_coursedesign cd on cd.id = cda.coursedesign left join t_major m on m.id = cda.major left join t_user u on u.id = cda.creator
cda.semester = #{semester }
AND cda.creator = #{creator }
AND u.college = #{college }
AND cda.major = #{major }

mapper.update:

update t_cdesign_account
set status = #{status }
where id = #{id }

mapper.select:
