mapper.select:
select gda.id as gda_id, gda.semester as gda_semester, gda.major as gda_major, gda.grade as gda_grade, gda.class_num as gda_class_num, gda.stu_num as gda_stu_num, gda.week_num as gda_week_num, gda.factor as gda_factor, gda.workload as gda_workload, gda.campus as gda_campus, gda.period as gda_period, gda.creator as gda_creator, gda.create_date as gda_create_date, gda.remark as gda_remark, gda.status as gda_status, m.id as m_id, m.name as m_name, u.id as u_id, u.name as u_name from t_grdtdesign_account gda left join t_major m on m.id = gda.major left join t_user u on u.id = gda.creator
gda.semester = #{semester }
AND gda.creator = #{creator }
AND u.college = #{college }
AND gda.major = #{major }

select gda.id as gda_id, gda.semester as gda_semester, gda.major as gda_major, gda.grade as gda_grade, gda.class_num as gda_class_num, gda.stu_num as gda_stu_num, gda.week_num as gda_week_num, gda.factor as gda_factor, gda.workload as gda_workload, gda.campus as gda_campus, gda.period as gda_period, gda.creator as gda_creator, gda.create_date as gda_create_date, gda.remark as gda_remark, gda.status as gda_status, m.id as m_id, m.name as m_name, u.id as u_id, u.name as u_name from t_grdtdesign_account gda left join t_major m on m.id = gda.major left join t_user u on u.id = gda.creator
gda.semester = #{semester }
AND u.college = #{college }
AND gda.major = #{major }
AND gda.creator = #{creator }
